// DataEntryForm.js

import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const DataEntryForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    week: 'Week 1',
    school: '',
    staffName: 'Staff 1',
    competenciesMet: '',
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!formData.name || !formData.week || !formData.school || !formData.staffName || !formData.competenciesMet) {
        console.error('All fields are required');
        return;
      }

      const response = await fetch('/api/entries', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const result = await response.json();
      console.log(result);
    } catch (error) {
      console.error('Error submitting data:', error);
    }
  };

  return (
    <form className="data-entry-form p-4">
      <div className="mb-3">
        <label htmlFor="name" className="form-label">Name:</label>  <input
        type="text"
        id="name"
        name="name"
        value={formData.name}
        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
      />
      </div>

      <label htmlFor="week">Week:</label>
      <select id="week" name="week" value={formData.week} onChange={(e) => setFormData({ ...formData, week: e.target.value })}>
        {[...Array(10).keys()].map((week) => (
          <option key={week + 1}>Week {week + 1}</option>
        ))}
      </select>

      <label htmlFor="school">School:</label>
      <input
        type="text"
        id="school"
        name="school"
        value={formData.school}
        onChange={(e) => setFormData({ ...formData, school: e.target.value })}
      />

      <label htmlFor="staffName">Staff Name:</label>
      <select
        id="staffName"
        name="staffName"
        value={formData.staffName}
        onChange={(e) => setFormData({ ...formData, staffName: e.target.value })}
      >
        {[...Array(20).keys()].map((staff) => (
          <option key={staff + 1}>Staff {staff + 1}</option>
        ))}
      </select>

      <label htmlFor="competenciesMet">Competencies Met:</label>
      <textarea
        id="competenciesMet"
        name="competenciesMet"
        value={formData.competenciesMet}
        onChange={(e) => setFormData({ ...formData, competenciesMet: e.target.value })}
      />

    <button type="submit" className="btn btn-primary">Submit</button>
    </form>
  );
};

export default DataEntryForm;
